// index.js
require('dotenv').config();
const express = require('express');
const cors = require('cors');
const { GoogleGenerativeAI } = require('@google/generative-ai');

const app = express();
app.use(cors());
app.use(express.json());

const genAI = new GoogleGenerativeAI(process.env.GEMINI_API_KEY);

app.post('/api/analyze', async (req, res) => {
  try {
    const user = req.body;
    const prompt = `
      Analyze the following user's profile:
      City: ${user.city}, Age: ${user.age}, Gender: ${user.gender}, Education: ${user.education}, Career: ${user.career}

      Please provide:
      1. Top 5 employable skills for their city in the next 5 years
      2. Skill gap analysis and readiness score (out of 100%)
      3. Learning plan (action steps)
      4. Relevant free courses

      Format as JSON.
    `;
    const model = genAI.getGenerativeModel({ model: "gemini-2.5-flash" });
    const result = await model.generateContent(prompt);
    res.json({ response: JSON.parse(result.response.text()) });
  } catch (e) {
    res.status(500).json({ error: e.message });
  }
});

app.listen(3001, () => console.log('Backend running on port 3001'));


// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
