import React, { useState } from 'react';
import ProfileForm from './components/ProfileForm';

const sideBox = {
  width: "27%",
  minWidth: 240,
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  background: "linear-gradient(131deg, #e3f6fd 20%, #c9eeff 90%)",
  padding: "2rem 1rem",
  color: "#274777",
  borderRadius: "18px",
  margin: "1rem",
  boxShadow: "0 0 18px #eef"
};

const mainRow = {
  width: "100vw",
  minHeight: "100vh",
  display: "flex",
  flexDirection: "row",
  justifyContent: "space-between",
  alignItems: "center",
  background: "linear-gradient(115deg,#fafbfc 40%, #dbeefe 100%)",
  overflow: "hidden"
};

function App() {
  const [output, setOutput] = useState(null);

  function handleSubmit(form) {
    alert('Form submitted: ' + JSON.stringify(form, null, 2));
    setOutput(form);
  }

  return (
    <div style={mainRow}>
      {/* Left Side */}
      <div style={sideBox}>
        <h2 style={{ color: "#1976d2", fontSize: "2rem", marginBottom: ".6rem" }}>Land Your Vision!</h2>
        <img
          src="https://unsplash.com/photos/love-to-learn-pencil-signage-on-wall-near-walking-man-WE_Kv_ZB1l0"
          alt="Vision"
          style={{width: "90%", borderRadius: "10px", marginBottom: "1rem", boxShadow: "0 0 8px #ddd"}}
        />
        <p>
          This is the first step to discovering the career that matches your true passion. <br/><br/>
          Build your learning path, upgrade your skills, and let India's top opportunities find you.
        </p>
      </div>

      {/* Center: Profile Form */}
      <div style={{width:"46%", minWidth:300}}>
        <ProfileForm onSubmit={handleSubmit} />
        {/* Output preview (for demo) */}
        {output && (
          <div style={{
            margin: "2rem 0", padding: "1rem", borderRadius: "10px",
            background: "#fff8e3", color: "#2c3a4f", fontWeight: "bold", fontSize:"1.05rem"
          }}>
            <h4>Profile Data:</h4>
            <pre>{JSON.stringify(output, null, 2)}</pre>
          </div>
        )}
      </div>

      {/* Right Side */}
      <div style={sideBox}>
        <h2 style={{ color:"#fb8500", fontSize:"2rem", marginBottom:".6rem" }}>Achieve Your Dreams!</h2>
        <img
          src="https://cdn.pixabay.com/photo/2016/10/22/20/48/laptop-1765717_1280.jpg"
          alt="Dreams"
          style={{width:"75%", borderRadius:"10px", marginBottom:"1rem", boxShadow:"0 0 8px #ddd"}}
        />
        <p>
          Upskill, learn, and grow with the help of AI-powered guidance. <br/><br/>
          Start your skill journey nowâ€”step boldly into your future!
        </p>
      </div>
    </div>
  );
}

export default App;
