import React, { useState } from 'react';
import ProfileForm from './components/ProfileForm';

// --- styles ---
const BG_ANIMATION = {
  position: "fixed",
  top:0, left:0,
  width:"100vw", height:"100vh",
  zIndex:0,
  pointerEvents: "none",
  background: "radial-gradient(circle, #fffbe7 0%, #e9f8ed 40%, #ffecd2 100%)",
  animation: "bgMove 22s linear infinite alternate"
};
const keyframes = `
@keyframes bgMove {
  0% {background-position: 0% 50%;}
  100% {background-position: 100% 50%;}
}`;
const IndiaBanner = {
  width:"100%", padding:"1rem 0",
  background:"linear-gradient(90deg,#ff671f 0%,#fff 35%,#46b729 80%)",
  textAlign:"center",
  fontWeight:"bold",
  fontSize:"2.24rem",
  letterSpacing:".04em",
  color:"#0c232e",
  boxShadow:"0 4px 16px #f1f1f1"
};
const mainRow = {
  width: "100vw",
  minHeight: "100vh",
  display: "flex",
  flexDirection: "row",
  justifyContent: "space-between",
  alignItems: "center",
  zIndex:1,
  position:"relative"
};
const sideBox = {
  width: "27%",
  minWidth: 240,
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  padding: "2rem 1rem",
  color: "#274777",
  borderRadius: "18px",
  margin: "1rem",
  background: "rgba(255,255,255,0.82)",
  boxShadow: "0 0 16px #eee"
};
const analysisBox = {
  margin: "1.5rem 0",
  padding: "1rem",
  background: "#fff",
  borderRadius: "10px",
  border: "1px solid #e3f6fd",
  boxShadow: "0 2px 6px #e3e3e3",
  fontWeight: "normal",
  color: "#231a18",
  lineHeight: 1.7,
  fontSize: "1.05rem"
};
const separator = {
  height: "3px",
  margin: "1.8rem 0",
  background: "linear-gradient(90deg,#ff671f 0%,#fff 70%,#46b729 100%)",
  border: "none",
  borderRadius: "2px"
};
const downloadButtonStyle = {
  background: "linear-gradient(90deg, #46b729 0%, #2e7d32 100%)",
  color: "#fff",
  border: "none",
  padding: "0.75rem 1.5rem",
  borderRadius: "10px",
  fontWeight: "bold",
  fontSize: "1rem",
  cursor: "pointer",
  marginTop: "1rem",
  display: "block",
  marginLeft: "auto",
  marginRight: "auto"
};

function App() {
  const [output, setOutput] = useState(null);

  async function handleSubmit(form) {
    const res = await fetch("http://localhost:3001/api/analyze", {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(form)
    });
    const data = await res.json();
    setOutput(data.response);
  }

  // Download as Word document
  function downloadAsWord() {
    if (!output) return;

    // Build formatted content
    let content = `Career Guidance Report\n\n`;

    if (output.job_market_forecast) {
      content += `General Analysis (Job Market Forecast):\n${output.job_market_forecast}\n\n`;
    }

    if (output.skills_needed) {
      content += `Skills Required:\n`;
      output.skills_needed.forEach(skill => {
        content += `‚Ä¢ ${skill}\n`;
      });
      content += `\n`;
    }

    if (output.skill_gap) {
      content += `Skill Gap:\n`;
      output.skill_gap.forEach(gap => {
        content += `‚Ä¢ ${gap}\n`;
      });
      content += `\n`;
    }

    if (output.recommended_courses_city) {
