import React, { useState } from 'react';
import ProfileForm from './components/ProfileForm';

const BG_ANIMATION = {
  position: "fixed",
  top:0, left:0,
  width:"100vw", height:"100vh",
  zIndex:0,
  pointerEvents: "none",
  background: "radial-gradient(circle, #fffbe7 0%, #e9f8ed 40%, #ffecd2 100%)",
  animation: "bgMove 22s linear infinite alternate"
};
const keyframes = `
@keyframes bgMove {
  0% {background-position: 0% 50%;}
  100% {background-position: 100% 50%;}
}`;

const IndiaBanner = {
  width:"100%", padding:"1rem 0",
  background:"linear-gradient(90deg,#ff671f 0%,#fff 35%,#46b729 80%)",
  textAlign:"center",
  fontWeight:"bold",
  fontSize:"2.24rem",
  letterSpacing:".04em",
  color:"#0c232e",
  boxShadow:"0 4px 16px #f1f1f1"
};

const mainRow = {
  width: "100vw",
  minHeight: "100vh",
  display: "flex",
  flexDirection: "row",
  justifyContent: "space-between",
  alignItems: "center",
  zIndex:1,
  position:"relative"
};

const sideBox = {
  width: "27%",
  minWidth: 240,
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  padding: "2rem 1rem",
  color: "#274777",
  borderRadius: "18px",
  margin: "1rem",
  background: "rgba(255,255,255,0.82)",
  boxShadow: "0 0 16px #eee"
};

function App() {
  const [output, setOutput] = useState(null);

  async function handleSubmit(form) {
  const res = await fetch("http://localhost:3001/api/analyze", {
    method: "POST",
    headers: {"Content-Type": "application/json"},
    body: JSON.stringify(form)
  });
  const data = await res.json();
  setOutput(data.response);
}


  return (
    <>
      {/* Add animated background */}
      <div style={BG_ANIMATION}></div>
      {/* Inject keyframes for animation */}
      <style>{keyframes}</style>

      {/* Indian Banner */}
      <div style={IndiaBanner}>
        üáÆüá≥ <span style={{color:"#ff671f"}}>India Upfront</span> ‚Äì Skilling India <span style={{color:"#46b729"}}>üöÄ</span>
      </div>

      <div style={mainRow}>
        {/* Left Side */}
        <div style={sideBox}>
          <h2 style={{ color: "#e76f51", fontSize: "2rem", marginBottom: ".6rem" }}>
            Vision to Reality <span role="img" aria-label="lightbulb">üí°</span>
          </h2>
          <img
            src="https://cdn.pixabay.com/photo/2015/01/08/18/24/entrepreneur-593358_1280.jpg"
            alt="Vision"
            style={{width: "80%", borderRadius: "10px", marginBottom: "1rem", boxShadow: "0 0 8px #ddd"}}
          />
          <p style={{fontSize:"1.12rem"}}>
            "Skill up, step up." <br/><span style={{color:"#1976d2"}}>Your vision needs action!</span> <br/>
            Chart your journey in a rapidly growing India.<br/>
            <span role="img" aria-label="flag">üáÆüá≥</span> <span role="img" aria-label="rocket">üöÄ</span>
          </p>
        </div>

        {/* Center: Profile Form */}
        <div style={{width:"46%", minWidth:300, zIndex:2}}>
          <ProfileForm onSubmit={handleSubmit} />
          {/* Output preview (for demo) */}
          {output && (
            <div style={{
              margin: "2rem 0", padding: "1rem", borderRadius: "10px",
              background: "#fff8e3", color: "#2c3a4f", fontWeight: "bold", fontSize:"1.05rem"
            }}>
              <h4>Profile Data:</h4>
              <pre>{JSON.stringify(output, null, 2)}</pre>
            </div>
          )}
        </div>

        {/* Right Side */}
        <div style={sideBox}>
          <h2 style={{ color:"#46b729", fontSize:"2rem", marginBottom:".6rem" }}>
            Achieve Your Dreams <span role="img" aria-label="trophy">üèÜ</span>
          </h2>
          <img
            src="https://picjumbo.com/dream-drive-on-grossglockner-mountain-road/"
            alt="Dreams"
            style={{width:"74%", borderRadius:"10px", marginBottom:"1rem", boxShadow:"0 0 8px #ddd"}}
          />
          <p style={{fontSize:"1.12rem"}}>
            "Your skill, India's strength."<br/>
            <span style={{color:"#fb8500"}}>Start now, inspire tomorrow!</span> <br/>
            <span role="img" aria-label="sparkles">‚ú®</span> <span role="img" aria-label="graduate">üéì</span>
          </p>
        </div>
      </div>
    </>
  );
}

export default App;
