import React, { useState } from 'react';
import ProfileForm from './components/ProfileForm';

// Styles
const BG_ANIMATION = {
  position: "fixed",
  top:0, left:0,
  width:"100vw", height:"100vh",
  zIndex:0,
  pointerEvents: "none",
  background: "radial-gradient(circle, #fffbe7 0%, #e9f8ed 40%, #ffecd2 100%)",
  animation: "bgMove 22s linear infinite alternate"
};
const keyframes = `
@keyframes bgMove {
  0% {background-position: 0% 50%;}
  100% {background-position: 100% 50%;}
}`;
const IndiaBanner = {
  width:"100%", padding:"1rem 0",
  background:"linear-gradient(90deg,#ff671f 0%,#fff 35%,#46b729 80%)",
  textAlign:"center",
  fontWeight:"bold",
  fontSize:"2.24rem",
  letterSpacing:".04em",
  color:"#0c232e",
  boxShadow:"0 4px 16px #f1f1f1"
};
const topMotivationSection = {
  textAlign: "center",
  padding: "2rem 0",
  zIndex: 1,
  position: "relative"
};
const bigMotivationText = {
  fontSize: "3rem",
  fontWeight: "bold",
  background: "linear-gradient(90deg, #e76f51, #fb8500)",
  WebkitBackgroundClip: "text",
  WebkitTextFillColor: "transparent",
  marginBottom: "0.5rem"
};
const analysisBox = {
  maxWidth: "95%",
  width: "1200px",
  margin: "1.5rem auto",
  padding: "1.5rem",
  background: "#fff",
  borderRadius: "12px",
  border: "2px solid #e3f6fd",
  boxShadow: "0 2px 10px #e3e3e3",
  fontWeight: "normal",
  color: "#231a18",
  lineHeight: 1.7,
  fontSize: "1.05rem"
};
const separator = {
  height: "3px",
  margin: "1.8rem 0",
  background: "linear-gradient(90deg,#ff671f 0%,#fff 70%,#46b729 100%)",
  border: "none",
  borderRadius: "2px"
};
const downloadButtonStyle = {
  background: "linear-gradient(90deg, #46b729 0%, #2e7d32 100%)",
  color: "#fff",
  border: "none",
  padding: "0.85rem 1.5rem",
  borderRadius: "10px",
  fontWeight: "bold",
  fontSize: "1rem",
  cursor: "pointer",
  marginTop: "1rem"
};

function App() {
  const [output, setOutput] = useState(null);
  const [userName, setUserName] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('English');

  async function handleSubmit(form) {
    setUserName(form.name);
    const res = await fetch("http://localhost:3001/api/analyze", {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(form)
    });
    const data = await res.json();
    setOutput(data.response);
  }

  // Download as Word document in selected language
  async function downloadAsWord() {
    if (!output) return;

    // Translate content to selected language
    const translatedContent = await translateContent(output, selectedLanguage, userName);

    // Create blob and download
    const blob = new Blob([translatedContent], { type: 'application/msword' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `Career_Guidance_Report_${selectedLanguage}.doc`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  }

  // Simple translation function
  async function translateContent(output, language, name) {
    const translations = {
      'English': {
        title: 'Career Guidance Report',
        greeting: `Hi ${name}`,
        forecast: 'General Analysis (Job Market Forecast):',
        skills: 'Skills Required:',
        gap: 'Skill Gap:',
        courses_city: 'Recommended Courses and Resources (Your City):',
        courses_abroad: 'Recommended Courses and Resources (Outside India):',
        opportunities: 'Career Opportunities (Your City, 0‚Äì3 Years):'
      },
      'Hindi': {
        title: '‡§ï‡§∞‡§ø‡§Ø‡§∞ ‡§Æ‡§æ‡§∞‡•ç‡§ó‡§¶‡§∞‡•ç‡§∂‡§® ‡§∞‡§ø‡§™‡•ã‡§∞‡•ç‡§ü',
        greeting: `‡§®‡§Æ‡§∏‡•ç‡§§‡•á ${name}`,
        forecast: '‡§∏‡§æ‡§Æ‡§æ‡§®‡•ç‡§Ø ‡§µ‡§ø‡§∂‡•ç‡§≤‡•á‡§∑‡§£ (‡§®‡•å‡§ï‡§∞‡•Ä ‡§¨‡§æ‡§ú‡§æ‡§∞ ‡§™‡•Ç‡§∞‡•ç‡§µ‡§æ‡§®‡•Å‡§Æ‡§æ‡§®):',
        skills: '‡§Ü‡§µ‡§∂‡•ç‡§Ø‡§ï ‡§ï‡•å‡§∂‡§≤:',
        gap: '‡§ï‡•å‡§∂‡§≤ ‡§Ö‡§Ç‡§§‡§∞:',
        courses_city: '‡§Ö‡§®‡•Å‡§∂‡§Ç‡§∏‡§ø‡§§ ‡§™‡§æ‡§†‡•ç‡§Ø‡§ï‡•ç‡§∞‡§Æ ‡§î‡§∞ ‡§∏‡§Ç‡§∏‡§æ‡§ß‡§® (‡§Ü‡§™‡§ï‡§æ ‡§∂‡§π‡§∞):',
        courses_abroad: '‡§Ö‡§®‡•Å‡§∂‡§Ç‡§∏‡§ø‡§§ ‡§™‡§æ‡§†‡•ç‡§Ø‡§ï‡•ç‡§∞‡§Æ ‡§î‡§∞ ‡§∏‡§Ç‡§∏‡§æ‡§ß‡§® (‡§≠‡§æ‡§∞‡§§ ‡§ï‡•á ‡§¨‡§æ‡§π‡§∞):',
        opportunities: '‡§ï‡§∞‡§ø‡§Ø‡§∞ ‡§Ö‡§µ‡§∏‡§∞ (‡§Ü‡§™‡§ï‡§æ ‡§∂‡§π‡§∞, 0-3 ‡§µ‡§∞‡•ç‡§∑):'
      },
      'Tamil': {
        title: '‡Æ§‡Øä‡Æ¥‡Æø‡Æ≤‡Øç ‡Æµ‡Æ¥‡Æø‡Æï‡Ææ‡Æü‡Øç‡Æü‡ØÅ‡Æ§‡Æ≤‡Øç ‡ÆÖ‡Æ±‡Æø‡Æï‡Øç‡Æï‡Øà',
        greeting: `‡Æµ‡Æ£‡Æï‡Øç‡Æï‡ÆÆ‡Øç ${name}`,
        forecast: '‡Æ™‡Øä‡Æ§‡ØÅ‡Æµ‡Ææ‡Æ© ‡Æ™‡Æï‡ØÅ‡Æ™‡Øç‡Æ™‡Ææ‡ÆØ‡Øç‡Æµ‡ØÅ (‡Æµ‡Øá‡Æ≤‡Øà ‡Æö‡Æ®‡Øç‡Æ§‡Øà ‡ÆÆ‡ØÅ‡Æ©‡Øç‡Æ©‡Æ±‡Æø‡Æµ‡Æø‡Æ™‡Øç‡Æ™‡ØÅ):',
        skills: '‡Æ§‡Øá‡Æµ‡Øà‡ÆØ‡Ææ‡Æ© ‡Æ§‡Æø‡Æ±‡Æ©‡Øç‡Æï‡Æ≥‡Øç:',
        gap: '‡Æ§‡Æø‡Æ±‡Æ©‡Øç ‡Æá‡Æü‡Øà‡Æµ‡ØÜ‡Æ≥‡Æø:',
        courses_city: '‡Æ™‡Æ∞‡Æø‡Æ®‡Øç‡Æ§‡ØÅ‡Æ∞‡Øà‡Æï‡Øç‡Æï‡Æ™‡Øç‡Æ™‡Æü‡Øç‡Æü ‡Æ™‡Ææ‡Æü‡Æ®‡ØÜ‡Æ±‡Æø‡Æï‡Æ≥‡Øç ‡ÆÆ‡Æ±‡Øç‡Æ±‡ØÅ‡ÆÆ‡Øç ‡ÆÜ‡Æ§‡Ææ‡Æ∞‡Æô‡Øç‡Æï‡Æ≥‡Øç (‡Æâ‡Æô‡Øç‡Æï‡Æ≥‡Øç ‡Æ®‡Æï‡Æ∞‡ÆÆ‡Øç):',
        courses_abroad: '‡Æ™‡Æ∞‡Æø‡Æ®‡Øç‡Æ§‡ØÅ‡Æ∞‡Øà‡Æï‡Øç‡Æï‡Æ™‡Øç‡Æ™‡Æü‡Øç‡Æü ‡Æ™‡Ææ‡Æü‡Æ®‡ØÜ‡Æ±‡Æø‡Æï‡Æ≥‡Øç ‡ÆÆ‡Æ±‡Øç‡Æ±‡ØÅ‡ÆÆ‡Øç ‡ÆÜ‡Æ§‡Ææ‡Æ∞‡Æô‡Øç‡Æï‡Æ≥‡Øç (‡Æá‡Æ®‡Øç‡Æ§‡Æø‡ÆØ‡Ææ‡Æµ‡Æø‡Æ±‡Øç‡Æï‡ØÅ ‡Æµ‡ØÜ‡Æ≥‡Æø‡ÆØ‡Øá):',
        opportunities: '‡Æ§‡Øä‡Æ¥‡Æø‡Æ≤‡Øç ‡Æµ‡Ææ‡ÆØ‡Øç‡Æ™‡Øç‡Æ™‡ØÅ‡Æï‡Æ≥‡Øç (‡Æâ‡Æô‡Øç‡Æï‡Æ≥‡Øç ‡Æ®‡Æï‡Æ∞‡ÆÆ‡Øç, 0-3 ‡ÆÜ‡Æ£‡Øç‡Æü‡ØÅ‡Æï‡Æ≥‡Øç):'
      },
      'Telugu': {
        title: '‡∞ï‡±Ü‡∞∞‡±Ä‡∞∞‡±ç ‡∞Æ‡∞æ‡∞∞‡±ç‡∞ó‡∞¶‡∞∞‡±ç‡∞∂‡∞ï‡∞§‡±ç‡∞µ ‡∞®‡∞ø‡∞µ‡±á‡∞¶‡∞ø‡∞ï',
        greeting: `‡∞®‡∞Æ‡∞∏‡±ç‡∞ï‡∞æ‡∞∞‡∞Ç ${name}`,
        forecast: '‡∞∏‡∞æ‡∞ß‡∞æ‡∞∞‡∞£ ‡∞µ‡∞ø‡∞∂‡±ç‡∞≤‡±á‡∞∑‡∞£ (‡∞â‡∞¶‡±ç‡∞Ø‡±ã‡∞ó ‡∞Æ‡∞æ‡∞∞‡±ç‡∞ï‡±Ü‡∞ü‡±ç ‡∞Ö‡∞Ç‡∞ö‡∞®‡∞æ):',
